mapper.select:
SELECT COUNT(*)>=1
FROM broker_info
WHERE BINARY bork_id = #{id}

SELECT COUNT(*)=1
FROM broker_info
WHERE BINARY bork_id = #{id}
    AND BINARY bork_pwd = #{pwd}

SELECT bork_id, bork_name, bork_image_file
FROM broker_info
WHERE BINARY bork_id = #{id}
    AND BINARY bork_pwd = #{pwd}

SELECT COUNT(*)=1
FROM broker_office_info
WHERE boi_seq = #{seq}

SELECT bork_status
FROM broker_info
WHERE bork_seq = #{broker_no}

mapper.update:
UPDATE broker_info
SET bork_status = #{status}
WHERE bork_seq = #{broker_no}

mapper.insert:
INSERT INTO broker_office_info
(
    boi_name, boi_address, boi_phone, boi_master_name, boi_reg_number, 
    boi_image_file
)
VALUES
(
    #{boi_name}, #{boi_address}, #{boi_phone}, #{boi_master_name}, #{boi_reg_number}, 
    #{boi_image_file}
)

mapper.select:
SELECT COUNT(*) = 1
FROM broker_office_info
WHERE replace(boi_name,' ','') = #{name} 
    OR boi_reg_number = #{reg_no}

mapper.update:
UPDATE broker_office_info
boi_address=#{boi_address}, boi_phone=#{boi_phone}, boi_master_name=#{boi_master_name}, boi_image_file=#{boi_image_file},
WHERE boi_seq=#{boi_seq}
