mapper.insert:
INSERT INTO building_info
(
    bi_name, bi_total_floor, bi_total_parking, bi_elevator, bi_use_type, 
    bi_use_accepted_dt, bi_address, bi_longitude, bi_latitude
)
VALUES
(
    #{bi_name}, #{bi_total_floor}, #{bi_total_parking}, #{bi_elevator}, #{bi_use_type}, 
    #{bi_use_accepted_dt}, #{bi_address}, #{bi_longitude}, #{bi_latitude}
)

mapper.select:
SELECT COUNT(*) = 1
FROM building_info
WHERE replace(bi_address,' ','') = #{address}

mapper.update:
UPDATE building_info

    bi_name=#{building_info.bi_name}
    bi_total_floor=#{building_info.bi_total_floor}
    bi_total_parking=#{building_info.bi_total_parking}
    bi_elevator=#{building_info.bi_elevator}
    bi_use_type=#{building_info.bi_use_type}
    bi_use_accepted_dt=#{building_info.bi_use_accepted_dt}
    bi_address=#{building_info.bi_address}
    bi_longitude=#{building_info.bi_longitude}
    bi_latitude=#{building_info.bi_latitude}

WHERE 
    bi_seq=#{building_info.bi_seq}

mapper.select:
SELECT COUNT(*) = 1 
FROM building_info
WHERE bi_seq = #{building_seq}

